import { Add, Remove } from '@material-ui/icons';
import React from 'react';
import styled from 'styled-components';
import Footer from '../Footer';
import Info from '../Info';
import Navbar from '../Navbar';
import News from '../News';

const Container = styled.div`
`;
const Wrap = styled.div `
    padding: 20px;
    display: flex;
`;
const ImgContainer = styled.div `
    flex: 1;
`;
const Image = styled.img `
    width: 80%;
    height: 70vh;
    object-fit: cover;
`;
const InfoContainer = styled.div `
    flex: 1;
    padding: 0px 50px;
`;
const Title = styled.h1 `
    font-weight: 200;
`;
const Descriptions = styled.p `
    margin: 20px 0px;
`;
const Price = styled.span `
    font-weight: 100;
    font-size: 40px;
`;
const FilterWrap = styled.div`
    display: flex;
    justify-content: space-between;
    width: 50%;
    margin: 30px 0px;
`;
const Filter = styled.div `
    display: flex;
    align-items: center;
`;
const FilterColor = styled.div `
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background-color: ${props=>props.color};
    border: solid 0.5px black;
    margin: 0px 5px;
    cursor: pointer;
`;
const FilterTitle = styled.span `
    font-size: 20px;
    font-weight: 200;
`;
const FilterSize = styled.select `
    margin-left: 10px;
    padding: 5px;

`;
const FilterOption = styled.option `
`;
const Increase = styled.div`
`;
const Decrease = styled.div`
`;
const AmountWrap = styled.div `
`;
const Amount = styled.div `
`;

const Product = () => {
  return (
    <Container>
        <Navbar/>
        <Info/>
        <Wrap>
            <ImgContainer>
                <Image src='https://i.ibb.co/93C9NkX/Details1.png'/>
            </ImgContainer>
            <InfoContainer>
                <Title>Standard Short Sleeves Shirt - Blue Buds on Gray</Title>
                <Descriptions>Men standard shirt with straight cutting (non-body fit).
                    Suitable for casual or formal occasion.
                    Fabrics of cotton hand-dyed Batik, ethically made in Indonesia.</Descriptions>
                <Price>Rp 69.000</Price>
                <FilterWrap>
                    <Filter>
                        <FilterTitle>Color</FilterTitle>
                        <FilterColor color='black'/>
                        <FilterColor color='white'/>
                        <FilterColor color='gray'/>
                    </Filter>
                    <Filter>
                        <FilterTitle>Size</FilterTitle>
                        <FilterSize>
                            <FilterOption>XS</FilterOption>
                            <FilterOption>S</FilterOption>
                            <FilterOption>M</FilterOption>
                            <FilterOption>L</FilterOption>
                            <FilterOption>XL</FilterOption>
                        </FilterSize>
                    </Filter>
                </FilterWrap>

                <Increase>
                    <AmountWrap>
                        <Remove/>
                        <Amount>1</Amount>
                        <Add/>
                    </AmountWrap>
                </Increase>

            </InfoContainer>
        </Wrap>
        <News/>
        <Footer/>
    </Container>
  )
}

export default Product
