import { ArrowBackIosOutlined, ArrowForwardIosOutlined } from '@material-ui/icons';
import React from 'react'
import styled from 'styled-components';

//  Styled sections
const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    background-color: #FFF;
    position: relative;
    overflow: hidden;
`
const Arrow = styled.div`
    width: 50px;
    height: 50px;
    background-color: transparent;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: ${props=> props.direction === 'left' && '10px'};
    right: ${props=> props.direction === 'right' && '10px'};
    margin: auto;
    cursor: pointer;
    opacity: 0.5;
`
const Wrapper = styled.div`
    height: 100%;
    display: flex;
`
const Slide = styled.div`
    display: flex;
    align-items: center;
    width: 100vw;
    height: 100vh;
`
const Image = styled.img `
    height: 80%;
`
//  aka info container
const ContainerDetail = styled.div `
    flex: 1;
    padding: 50px;
`
const ImageContainer = styled.div`
    flex: 1;
    height: 100%;
`
const Title =  styled.h1`
    font-size: 45px;
`
const Description = styled.p `
    margin: 50px 0px;
    font-size: 20px;
    font-weight: 500;
`
const Prgrf = styled.p`
    font-size: 15px
`
const Button = styled.button `
    padding: 10px;
    font-size: 20px;
    background-color: transparent;
    cursor: pointer;
`

const Slider = () => {
  return (
    <Container>
        <Arrow direction='left'>
            <ArrowBackIosOutlined/>
        </Arrow>
        <Wrapper>

            <Slide>
            <ImageContainer>
                <Image src='https://i.ibb.co/54CgDnJ/Slider2.png'/>
            </ImageContainer>
            <ContainerDetail>
                <Title>MONOGRAM COLLECTIONS</Title>
                <Description>
                    MADE FOR SELF-EXPRESSION
                    <Prgrf>THIS MAKE FOR LOVE AND SECRET THAT ALLOWS YOU TO FEEL CONFIDENT</Prgrf>
                </Description>
                <Button>SHOP THE LOOK</Button>
            </ContainerDetail>
            </Slide>

            <Slide>
            <ImageContainer>
                <Image src='https://i.ibb.co/54CgDnJ/Slider2.png'/>
            </ImageContainer>
            <ContainerDetail>
                <Title>SUMMER COLLECTIONS</Title>
                <Description>MADE FOR SELF-EXPRESSION</Description>
                <Button>SHOP THE LOOK</Button>
            </ContainerDetail>
            </Slide>

            <Slide>
            <ImageContainer>
                <Image src='https://i.ibb.co/54CgDnJ/Slider2.png'/>
            </ImageContainer>
            <ContainerDetail>
                <Title>WOMEN COLLECTIONS</Title>
                <Description>MADE FOR SELF-EXPRESSION</Description>
                <Button>SHOP THE LOOK</Button>
            </ContainerDetail>
            </Slide>

        </Wrapper>
         <Arrow direction='right'>
            <ArrowForwardIosOutlined/>
        </Arrow>
    </Container>
  )
}

export default Slider
